import React from 'react';
import { useSelector, useDispatch } from 'react-redux';

import video0 from '../assets/Fireworks Display.mp4';
import video1 from '../assets/Pexels Videos 4513.mp4';
import video2 from '../assets/Time Lapse Video Of Aurora Borealis.mp4';
import video3 from '../assets/35960_woman_with_shaved_head_slowly_swims_closer_to_viewer_by_Omri_Ohana_Artgrid-HD_H264-HD.mp4';
import { resetSlider, sliderTransition } from '../store/actions/heroActions';

export const Landing = () => {
    const video = useSelector(state => state.heroReducer.video);
    const dispatch = useDispatch();

    const heroSlider = () => {
        if(video === 0.4) {
           dispatch(resetSlider()) 
        }else {
            dispatch(sliderTransition())
        }
    }

    setInterval(heroSlider, 10000)

    return (
        <div>
            <header>
                {video === 0 ? <video src={video0} width='100%' autoPlay muted /> : null}
                {video === 0.1 ? <video src={video2} width='100%' autoPlay muted /> : null}
                {video === 0.2 ? <video src={video2} width='100%' autoPlay muted /> : null}
                {video === 0.3 ? <video src={video3} width='100%' autoPlay muted /> : null}
            </header>
        </div>
    )
}
