import {
    CHAT,
    RETURN_WILDCARD_INFO
} from '../actions'

const initialState = {
    time: 0,
    test: [{
        message: ''
    }],
    wildcard: {}
}

export const chatBotReducer = (state = initialState, action) => {
    switch(action.type) {
        case CHAT:
            return {
                ...state,
                test: [...state.test, action.payload]
            }
        case CONVERSATION_ADD:
            return {
                ...state,
                time: state.time + 1
            }
        case CONVERSATION_MINUS:
            return {
                ...state,

            }
        case RETURN_WILDCARD_INFO:
            return {
                ...state,
                wildcard: state.wildcard
            }
        default:
            return state;
    }
}
