import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';

import image0 from '../assets/icons8-quill-pen-512.png';
import image1 from '../assets/icons8-snow-480 (1).png';
import image2 from '../assets/icons8-snow-480.png';
import image3 from '../assets/NewRomeMediaLogo.png';

import { resetSlider, sliderTransition } from '../store/actions/heroActions';

export const Landing = () => {
    const video = useSelector(state => state.heroReducer.video);
    const dispatch = useDispatch();
    const [sliderTimer, setSliderTimer] = useState(video)

    const heroSlider = () => {
        if(sliderTimer === 0.05) {
           dispatch(resetSlider()) 
        }else {
            dispatch(sliderTransition())
        }
    }

    window.setInterval(heroSlider, 12000)

    return (
        <div>
            <header>
                {sliderTimer === 0.01 ? <img src={image0} alt='text here' /> : null}
                {sliderTimer === 0.02 ? <img src={image1} alt='text here' /> : null}
                {sliderTimer === 0.03 ? <img src={image2} alt='text here' /> : null}
                {sliderTimer === 0.04 ? <img src={image3} alt='text here' /> : null}
            </header>
        </div>
    )
}
